use igem;

-- drop table plasmid;

CREATE table plasmid(
	id int(8) primary key auto_increment not null,
	name varchar(100) default "",
	PURPOSE varchar (1200) default "",
	DP_LAB varchar (80) default "",
	LAB_link varchar(80) default "",
	pub varchar(800) default "",
	pub_link varchar(80) default "",
	seq_link varchar(80) default "",
	BB_vector varchar(100) default "",
	BB_manu varchar(200) default "",
	BB_size varchar(40) default "",
	Vector_size  varchar(40) default "",
	Vector_type  varchar(200) default "",
	Mod2BB varchar(1200) default "",
	Bac_Re varchar(80) default "",
	Temp varchar(40) default "",
	Strain varchar(80) default "",
	Copy_nu varchar(120) default "",
	insert_name varchar(200) default "",
	insert_type varchar(200) default "",
	insert_size varchar(40) default "",
	Prom varchar(200) default "",
	mutation varchar(400) default "",
	tag varchar(200) default "",
	pe5 varchar(400) default "",
	pe3 varchar(400) default "",
	Document_url varchar(200) default "",
	Document varchar(200) default "",
	License varchar(120) default "",
	License_url varchar(80) default "",
	Cite varchar(400) default "",
	Cite_url varchar(80) default "",
	Cite_it1 varchar(1200) default "",
	Cite_it2 varchar(1200) default ""
);



